BodyContent.ST:
								
IL_FB_Instance(Output => Q8_1);
ST_FB_Instance(Output => Q8_2);
LD_FB_Instance(Output => Q8_3);
FBD_FB_Instance(Output => Q8_4);
SFC_FB_Instance(Output => Q8_5);
SFC_FB2_Instance(Output => Q8_6);

BodyContent.IL:
								
// S1 -> S2
CAL	S1TON(IN := S1, PT := T#1S)
LD	S1TON.Q
R		S1
S		S2
								
// S2 -> S1
CAL	S2TON(IN:= S2, PT := T#1s);
LD	S2TON.Q
R		S2
S		S1

// Output
LD	Output
ST	S1

BodyContent.ST:
								
// S1 -> S2
S1TON(IN := S1, PT := T#1S);
IF S1TON.Q THEN
	S1 := FALSE;
	S2 := TRUE;
END_IF
								
// S2 -> S1
S2TON(IN:= S2, PT := T#1s);
IF S2TON.Q THEN
	S2 := FALSE;
	S1 := TRUE;
END_IF

// Output
Output := S1;
